import React, { Component, PropTypes } from 'react';
import axios from 'axios';
import { Grid } from '@material-ui/core';
import Plot from 'react-plotly.js';
import LinearProgress from '@material-ui/core/LinearProgress';
import Typography from '@material-ui/core/Typography';
import Plotly from 'plotly.js';
class TestvsCases extends Component {
    constructor(props) {
        super(props);

    }
    state={
        tests:null,
        cases:null
    };
    componentDidMount(){
        // axios.get('http://127.0.0.1:5000/getStateMap').then(res=>{
        //     console.log(res.data);
        //     this.setState({
        //         tests:res.data.stats_data
        //     })

        // }).catch(err=>{
        //     console.log(err)
        // });
        var graphDiv = document.getElementById('statesDiv');
            Plotly.d3.csv(
                "https://raw.githubusercontent.com/leenapatil14/Udacity_Data_Science_Nanodegree_Repo/master/COVID_India/flask_project/web_app/data/India%20State%20Latitude%20and%20Longitude.csv",
                function(err, rows) {
                    function unpack(rows, key) {
                        return rows.map(function(row) {
                            return row[key];
                        });
                    }
                    var max_=Math.max(...(unpack(rows, "size")));
                    var low=10;
                    var high=45;
                    var min_=Math.min(...(unpack(rows, "size")));

                    var data = [
                        {
                            type: "scattermapbox",
                            text: unpack(rows, "State_Names"),
                            lon: unpack(rows, "longitude"),
                            lat: unpack(rows, "latitude"),
                            marker: { color: "#3f51b5", size: (unpack(rows, "size")).map(x=> (((x-min_)*(high-low))/(max_-min_))+low) ,min:5}
                        }
                    ];
                    console.log(data)
                    var layout = {
                        dragmode: "zoom",
                        mapbox: { style: "open-street-map", center: { lat: 21.30039105, lon: 76.13001949  }, zoom: 3 },
                        margin: { r: 0, t: 0, b: 0, l: 0 }
                    };

                    Plotly.newPlot(graphDiv, data, layout);
                }
            );
    }


    render() {
        var layout_={legend : {font : {size : 6, color : "#000"}}};

        let displaytests=this.state.tests? (
        <div>

        {this.state.tests.map(state=>
            <div className="alignCenter">
                <h4><center> Summary of {state.State_Name} </center></h4>
            <Plot data={state.chart}
            useResizeHandler={true}
            responsive={true}
            layout={state.layout}

            ></Plot>
        </div>
        )        

    }
        {/* <h4><center>Statewise summary of Confirmed Cases per day</center></h4>

        <div className="alignCenter">
            <Plot data={this.state.tests}
            useResizeHandler={true}
            responsive={true}
            layout={layout_}

            ></Plot>
        </div> */}
        </div>
        ):
        <div className="padding10per">
        <Typography color="textSecondary">Loading...</Typography>
        <LinearProgress color="secondary" />
        </div>;
        return(
        <Grid container>
                <Grid item sm={12} xs={12}>
                    {/* <div>{displaytests}</div> */}
                    <div id="statesDiv"></div>
                </Grid>

            </Grid>
        );
    }
}

