import React, { Component, PropTypes } from 'react';
import axios from 'axios';
import { Grid } from '@material-ui/core';
import Plot from 'react-plotly.js';
import CircularProgress from '@material-ui/core/CircularProgress';
class TestvsCases extends Component {
    constructor(props) {
        super(props);

    }
    state={
        testsData:null,
    };
    componentDidMount(){
        axios.get('/getTests').then(res=>{
            console.log(res);
            this.setState({
                testsData:res.data.data
            })
        }).catch(err=>{
            console.log(err)
        })
    }


    render() {
        var layout_={legend : {font : {size : 6, color : "#000"}}};
        let displaytests=this.state.testsData? (
        <div>
            <Plot data={this.state.testsData}
            useResizeHandler={true}
            responsive={true}
            layout={layout_}

            ></Plot>
        </div>
        ):
        <div className="padding10">
        <LinearProgress color="secondary" />
        </div>;
        return(
        <Grid container>
                <Grid item sm={12} xs={12}>
                    <h4><center>Summary of tests done per day</center></h4>
                <div>{displaytests}</div>
                </Grid>

            </Grid>
        );
    }
}

TestvsCases.propTypes = {

};

export default TestvsCases;
